import javax.servlet.RequestDispatcher;
import javax.servlet.ServletException;
import javax.servlet.annotation.WebServlet;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import java.io.IOException;
import java.util.ArrayList;
import java.util.List;

@WebServlet(name = "ServletDisplay", urlPatterns = "/listCustomer")
public class ServletDisplay extends HttpServlet {
    List<Customer> customers= new ArrayList<>();

    @Override
    public void init() throws ServletException {
        super.init();
        customers.add(new Customer("HIEU", "27/03/2002","DN", "data:image/jpeg;base64,"));
        customers.add(new Customer("PHUONG", "22/03/2002","DN", "data:image/jpeg;base64,"));

        customers.add(new Customer("AN", "21/05/2001","QN", "data:image/jpeg;base64,"));

        customers.add(new Customer("THANG", "28/01/2000","QN", "data:image/jpeg;base64,"));

    }

    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {

    }

    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        request.setAttribute("customersList", customers);

        RequestDispatcher dispatcher = request.getRequestDispatcher("display.jsp");
        dispatcher.forward(request,response);
    }
}
